function lenisSetup() {
    const lenis = new Lenis({
        duration: 1.2,
		easing: (t) => (t === 1 ? 1 : 1 - Math.pow(2, -10 * t)),
		direction: "vertical",
		gestureDirection: "vertical",
		smooth: true,
		smoothTouch: false,
		touchMultiplier: 2,
    })
    lenis.scrollTo(document.querySelector('.scrolllenis'))

    function raf(time) {
        lenis.raf(time)
        requestAnimationFrame(raf)
    }

    requestAnimationFrame(raf);
    
}


